import React, {useState, useEffect} from 'react';
import Avatar from "@material-ui/core/Avatar";
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Modal from "@material-ui/core/Modal";
import makeStyles from "@material-ui/core/styles/makeStyles";
import UseAnimations from "react-useanimations";
import heart from "react-useanimations/lib/heart";
import bookmark from "react-useanimations/lib/bookmark";
import TelegramIcon from "@material-ui/icons/Telegram";
import ModeCommentIcon from '@material-ui/icons/ModeComment';
import {database} from "./firebase";
import Input from "@material-ui/core/Input";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import InsertEmoticonIcon from '@material-ui/icons/'


function menuModalStyle() {
    const top = 50;
    const left = 50;

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
        backgroundColor: 'white'
    };
}
const useStyles = makeStyles((theme) => ({
    paper: {
        position: 'relative',
        width: 800,
        backgroundColor: '#ffffff',
        padding: theme.spacing(2, 4, 3)
    },
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1),
        },
    },
    small: {
        width: theme.spacing(3),
        height: theme.spacing(3),
    },
    large: {
        width: theme.spacing(8),
        height: theme.spacing(8),
    },
}));

function Post({userName, captions, imageUrl, postAvatar, postId}) {

    const [comments, setComments] = useState([]);
    const [comment, setComment] = useState('');
    const modalStyle = useState(menuModalStyle);
    const classes = useStyles();
    const [openMenuModal, setOpenMenuModal] = useState(false);

    useEffect(() => {
        let unsubscribe;
        if(postId){
            unsubscribe = database
                .collection("posts")
                .doc(postId)
                .collection("comments")
                .onSnapshot((snapshot => {
                    setComments(snapshot.docs.map((doc)=>doc.data()))
                }))
        }

        return() => {
            unsubscribe();
        };
    }, [postId]);

    const postComment = (event) => {

    }

    return(
        <div className="Post">

            <div className="modal-class-menu">
                <Modal
                    //this is the menu-modal for a post.
                    open={openMenuModal}
                    onClose={() => setOpenMenuModal(false)}>
                    <div
                        className={classes.paper}
                        style={modalStyle}>
                        {
                            <div className="ul-items">
                                <ul>
                                    <li>
                                        First List Item.
                                    </li>
                                </ul>
                            </div>
                        }
                    </div>
                </Modal>
            </div>

            <div className="avatar">
                <div className="avatar-left">
                    <Avatar className="post-header"
                            alt = "your-avatar"
                            src={postAvatar}>
                    </Avatar>
                    <h5 className="post-user-name-top">{userName}</h5>
                </div>
                <div className="avatar-right">
                    <ExpandMoreIcon
                        onClick={() => setOpenMenuModal(true)}
                        fontSize="30"> </ExpandMoreIcon>
                </div>
            </div>

            <div className="user-image-post">
                <img className = "user-image"
                     src={imageUrl}
                     alt="random-image"
                />
            </div>
            <div className="post-button-collection">
                <div className="post-buttons">
                    <div className="individual-post-button">
                        <UseAnimations
                            animationKey="heart"
                            size={30}
                            style={{ cursor: "pointer",padding: 100 }}
                            animation={heart}/>
                    </div>
                    <div className="individual-post-button">
                        <div className="comment-post-icon">
                            <ModeCommentIcon style={{ fontSize: 25 }} />
                        </div>
                    </div>
                    <div className="individual-post-button">
                        <div className="telegram-post-icon">
                            <TelegramIcon style={{ fontSize: 25 }} />
                        </div>
                    </div>
                </div>
                <div className="last-post-button">
                    <div className="individual-post-button">
                        <UseAnimations
                            animationKey="bookmark"
                            size={30}
                            style={{ cursor: "pointer",padding: 100 }}
                            animation={bookmark}/>
                    </div>
                </div>
            </div>
            <div>
                <h5 className= "post-user-name-bottom" >
                    <strong className="post-description-name">
                        {userName}
                    </strong>
                    {captions}
                </h5>
                <div className="post-comments">
                    <form className="comment-form">
                        <div className="class">
                            <Grid container spacing={1} alignItems="flex-end">
                                <Grid item>
                                    <InsertEmoticonIcon />
                                </Grid>
                                <Grid item>
                                    <input placeholder="Add comments..." />
                                </Grid>
                            </Grid>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default Post
