import React, {useState, useEffect} from 'react';
import './App.css';
import Post from './Post.js'
import {database} from "./firebase";

function App() {

    const [posts, setPosts] =useState([]);

    useEffect(() => {
        database.collection('posts').onSnapshot(snapshot => {
            setPosts(snapshot.docs.map(doc => ({
                id,
                //id is the document id in firebase.
                post: doc.data()})));// this is all the data in a document.
        })
    }, [posts])

  return (
    <div className = "app">
      <div className= "nav-bar">
          <img className="nav-bar-image"
               src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
               alt="insta-letter-logo"
          />
      </div>

        {
            posts.map(({id, posts}) =>(
                //we're mapping the document id with every post so that react only loads new posts and the
                <Post
                    docid={id}
                    //key will get document id from firebase.

                    userName={posts.userName}
                    captions={posts.captions}
                    imageUrl={posts.imageUrl}
                    postAvatar={posts.postAvatar}/>
            ))
        }

    </div>
  );
}

export default App;
