import React from 'react';
import './Post.css';
import ReactDOM from "react-dom";
import Avatar from "@material-ui/core/Avatar";


function Post({userName, captions, imageUrl, postAvatar}) {

    return(
        <div className="Post">
                <div className="avatar">
                    <Avatar className="post-header"
                            alt = {userName}
                            src={postAvatar}>

                    </Avatar>
                    <h5 className="post-user-name-top">{userName}</h5>

                </div>

            {/* eslint-disable-next-line jsx-a11y/img-redundant-alt */}
            <img className = "user-image"
                 src={imageUrl}
                 alt="random-image"
            />
            <div>
                <h6 className= "post-user-name-bottom" >
                    <strong>
                        {userName}:
                        {/*this is the username below the post*/}
                        {/*we use strong to replicate the bold tag in HTML.*/}
                    </strong>
                    {captions}
                    {/*This is the user-set captions below the post.*/}
                </h6>
                {/*show different user comments*/}
                {/*add a comment... (user who sees the post can add a comment and post button.)*/}
            </div>
        </div>
    )
}

class MyCar extends React.Component{
    constructor(car) {
        super(car);
        this.state = {
            brand: "Mercedes",
            color: "Vanta-black",
            year: 2000,
            services: 5
        }
    }

    changeState = () => {
        this.setState({color: "White"});
    }

    render() {
        return (
            <div className="button">
                <h1>
                    This is my car. And this is of color: {this.state.color}.
                </h1>
                <button
                    type="button"
                    onClick={this.changeState}>
                    Change Color!
                </button>
            </div>
        );
    }
}

ReactDOM.render(<MyCar />, document.getElementById("root"))
export default Post
