import React from 'react';
import './Post.css';
import Avatar from "@material-ui/core/Avatar";

const userName = "padhs";
const captions = "This is my first instagram post.";
const imageUrl = "https://i.picsum.photos/id/866/536/354.jpg?hmac=tGofDTV7tl2rprappPzKFiZ9vDh5MKj39oa2D--gqhA";

function Post({userName, captions, imageUrl}) {
    return(
        <div className="Post">
                <div className="avatar">
                    <Avatar className="post-header"
                            alt = {userName}
                            src="https://previews.123rf.com/images/smith1979/smith19791905/smith1979190500006/123604417-funny-dog-black-and-white-vector-illustration-circular-shape-design.jpg">

                    </Avatar>
                    <h5 className="post-user-name-top">{userName}</h5>

                </div>

            {/* eslint-disable-next-line jsx-a11y/img-redundant-alt */}
            <img className = "user-image"
                 src={imageUrl}
                 alt="random-image"
            />
            <div>
                <h6 className= "post-user-name-bottom" >
                    <strong>
                        {userName}:
                        {/*this is the username below the post*/}
                        {/*we use strong to replicate the bold tag in HTML.*/}
                    </strong>
                    {captions}
                    {/*This is the user-set captions below the post.*/}
                </h6>
                {/*show different user comments*/}
                {/*add a comment... (user who sees the post can add a comment and post button.)*/}
            </div>
        </div>
    )
}

export default Post
