import React, {useEffect, useState} from "react";
import Picker from 'emoji-picker-react';
import Input from "@material-ui/core/Input";


function EmojiPicker(){

    const [comment, setComment] = useState('');

    const [chosenEmoji, setChosenEmoji] = useState(null);
    const onEmojiClick = (event, emojiObject) => {
        setChosenEmoji(emojiObject);
    }
    const finalComment = (
        <div>
            {comment}{chosenEmoji.emoji}
        </div>
    )
    useEffect(() => {
        setComment(comment);
    })


    return(
        <div className="emoji-picker-comment">
            <div className="emoji-field">
                {chosenEmoji ? (
                    <></>
                ) : (
                    <></>
                )}
                <Picker onEmojiClick={onEmojiClick}
                        groupNames={{
                            smileys_people: 'yellow faces',
                            animals_nature: 'cute dogs and also trees',
                            food_drink: 'milkshakes and more',
                            travel_places: 'I love trains',
                            activities: 'lets play a game',
                            objects: 'stuff',
                            symbols: 'more stuff',
                            flags: 'fun with flags',
                            recently_used: 'did I really use those?!',
                        }}/>
            </div>
            <div className="input-field">
                <Input
                    placeholder="Add a comment..."
                    value={comment}
                    type="text"
                    onChange={(comment) => setComment(comment.target.value)}/>
            </div>
            <div className="show-comment">
                {comment ? <div>
                    {finalComment}
                </div> : <></>}
            </div>
        </div>
    )
}

export default EmojiPicker
