import React from "react";
import { useLayer, useHover, Arrow } from "react-laag";

function Tooltip(content) {
    const [isOver, hoverProps] = useHover();

    const {
        triggerProps,
        layerProps,
        arrowProps,
        renderLayer
    } = useLayer({
        isOpen: isOver
    });

    return (
        <>
            {isOver &&
            renderLayer(
                <div className="tooltip" {...layerProps}>
                    {content}
                    <Arrow {...arrowProps} />
                </div>
            )}
        </>
    );
}
export default Tooltip
