import React from "react";

class Testing extends React.Component{
    constructor(methods) {
        super(methods);
        this.state = {
            brand: "Mercedes",
            color: "Black",
            year: 2500,
            price: 250000000
        }
    }

    componentDidMount() {
        setTimeout(() => {
            this.setState({color: "Magenta"})
        }, 2000)
    }

    getSnapshotBeforeUpdate(prevProps, prevState) {
        document.getElementById("beforeUpdate").innerHTML = "Before the update, my favorite color was: " + this.state.color;
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        document.getElementById("afterUpdate").innerHTML = "But after the update, my favorite color became: " + this.state.color;
    }

    render() {
        return (
            <div>
                <h1>
                    My car has my favorite color which is: {this.state.color}
                </h1>
                <div id = "beforeUpdate"></div>
                <div id = "afterUpdate"></div>
            </div>
        );
    }
}


export default Testing
